declare(strict_types=1);

namespace Tests\Parent\Model;

use Doctrine\ORM\Mapping as ORM;

trait GroupTrait
{
    /**
     * @ORM\Column(type="string")
     *
     * @var string
     */
    protected $name;

    /**
     * {@inheritdoc}
     */
    public function setName($name)
    {
        $this->name = $name;

        return $this;
    }

    /**
     * {@inheritdoc}
     */
    public function getName()
    {
        return $this->name;
    }
}
